<?phpfunction __autoload($classname){	include_once("control/$classname.php");}	$action = 'action_';	$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['act']){	case 'main':		$controller = new control_page();		break;	case 'view':		$controller = new control_page($_GET['id']);		break;	case 'edit' :		$controller = new control_page($_GET['id']);		break;	case 'new':		$controller = new control_page();		break;	default:		$controller = new control_page();}$controller->Request($action);